import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../services/backend.service';
import { DomSanitizer } from '@angular/platform-browser';
import { User } from '../models/User';

@Component({
  selector: 'app-basket',
  templateUrl: './basket.component.html',
  styleUrls: ['./basket.component.css'],
})
export class BasketComponent {
  ngOnInit(): void {}
  constructor(
    private http: HttpClient,
    private router: Router,
    public services: BackendService,
    private sanitizer: DomSanitizer
  ) {}
  goToDeliveryView() {
    this.router.navigate(['delivery-view']);
  }
  goToReservarionView() {
    this.router.navigate(['reservation-view']);
  }
  goToHome() {
    this.router.navigate(['customer']);
  }
  goToChangePassword() {
    this.router.navigate(['change-password']);
  }

  goToProfile() {
    this.router.navigate(['profile']);
  }
  goToRestaurantDetails(id: string) {
    this.services.currentRestaurantBeingViewedId = id;
    this.router.navigate(['restaurant-view']);
  }
  toggleBasket() {
    this.router.navigate(['basket']);
  }
  logout() {
    this.services.activeUser = new User(
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      ''
    );
    this.router.navigate(['']);
  }
  placeOrder(): void {
    console.log('Order placed:', this.services.order);
    this.services.makeOrder(this.services.order).subscribe((response) => {
      this.services.order = [];
      this.router.navigate(['customer']);
    });
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Orders</title>
    <base href="/" />
  </head>
  <body>
    <header>
      <div class="logo" (click)="goToHome()">Restaurant of cringe</div>
      <div class="buttons">
        <button (click)="goToDeliveryView()">Deliveries</button>
      </div>
      <div class="buttons">
        <button (click)="goToReservarionView()">Reservations</button>
      </div>
      <div class="buttons">
        <button (click)="goToChangePassword()">Change password</button>
      </div>
      <div class="buttons">
        <button (click)="goToProfile()">Profile</button>
      </div>
      <!-- <div class="buttons">
      <button (click)="goToRestaurants()">Restaurants</button>
    </div> -->
      <div class="buttons">
        <button (click)="logout()">Logout</button>
      </div>
      <div class="buttons">
        <button (click)="toggleBasket()">Basket</button>
      </div>
    </header>
    <div class="orders-container">
      <h1>Orders</h1>
      <div *ngFor="let order of this.services.order" class="order-card">
        <p><strong>Username:</strong> {{ order.madeByUsername }}</p>
        <p><strong>Dish ID:</strong> {{ order.dishId }}</p>
        <p><strong>Amount:</strong> {{ order.amount }}</p>
        <p><strong>Restaurant ID:</strong> {{ order.restaurantId }}</p>
        <p><strong>Restaurant Name:</strong> {{ order.restaurantName }}</p>
      </div>
    </div>

    <div class="place-order-section">
      <button (click)="placeOrder()">PLACE AN ORDER</button>
    </div>
    <div class="footer">
      <p>&copy; 2024 pravi majstor. All rights reserved.</p>
    </div>
  </body>
</html>
