import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../services/backend.service';
import { RestaurantInfo } from '../models/RestaurantInfo';
import { Restaurant } from '../models/Restaurant';
import { User } from '../models/User';
import {
  DomSanitizer,
  SafeHtml,
  SafeResourceUrl,
} from '@angular/platform-browser';
import { Reservation } from '../models/Reservation';

@Component({
  selector: 'app-restaurants-view',
  templateUrl: './restaurants-view.component.html',
  styleUrls: ['./restaurants-view.component.css'],
})
export class RestaurantsViewComponent {
  constructor(
    private http: HttpClient,
    private router: Router,
    public services: BackendService,
    private sanitizer: DomSanitizer
  ) {}
  goToDeliveryView() {
    this.router.navigate(['delivery-view']);
  }
  goToReservarionView() {
    this.router.navigate(['reservation-view']);
  }
  goToHome() {
    this.router.navigate(['customer']);
  }
  goToChangePassword() {
    this.router.navigate(['change-password']);
  }

  goToProfile() {
    this.router.navigate(['profile']);
  }
  goToRestaurantDetails(id: string) {
    this.services.currentRestaurantBeingViewedId = id;
    this.router.navigate(['restaurant-view']);
  }

  logout() {
    this.services.activeUser = new User(
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      ''
    );
    this.router.navigate(['']);
  }
  restaurantInfo!: RestaurantInfo;
  iframeHtml!: SafeHtml;
  ngOnInit() {
    this.services
      .getRestaurantInfo(this.services.currentRestaurantBeingViewedId)
      .subscribe((response) => {
        this.restaurantInfo = response;
        this.iframeHtml = this.sanitizer.bypassSecurityTrustHtml(
          response.restaurant.embeddedMap
        );
      });
  }

  goToOrderFood() {
    this.router.navigate(['order-food']);
  }
  toggleBasket() {
    this.router.navigate(['basket']);
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Restaurant Info</title>
    <base href="/" />
  </head>
  <body>
    <header>
      <div class="roc-logo" (click)="goToHome()">Restaurant of cringe</div>
      <div class="roc-buttons">
        <button (click)="goToDeliveryView()">Deliveries</button>
      </div>
      <div class="roc-buttons">
        <button (click)="goToReservarionView()">Reservations</button>
      </div>
      <div class="roc-buttons">
        <button (click)="goToChangePassword()">Change password</button>
      </div>
      <div class="roc-buttons">
        <button (click)="goToProfile()">Profile</button>
      </div>
      <!-- <div class="roc-buttons">
        <button (click)="goToRestaurants()">Restaurants</button>
      </div> -->
      <div class="roc-buttons">
        <button (click)="logout()">Logout</button>
      </div>
      <div class="roc-buttons">
        <button (click)="toggleBasket()">Basket</button>
      </div>
    </header>
    <div class="restaurant-info" *ngIf="restaurantInfo">
      <h1 class="restaurant-name">{{ restaurantInfo.restaurant.name }}</h1>
      <div class="button-container">
        <button (click)="goToOrderFood()">Order Food</button>
        <button (click)="toggleBasket()">Basket</button>
      </div>

      <p><strong>Address:</strong> {{ restaurantInfo.restaurant.address }}</p>
      <p><strong>Type:</strong> {{ restaurantInfo.restaurant.type }}</p>
      <p>
        <strong>Phone Number:</strong>
        {{ restaurantInfo.restaurant.phoneNumber }}
      </p>

      <h2>Comments</h2>
      <div class="comments">
        <div *ngFor="let comment of restaurantInfo.comments" class="comment">
          <p><strong>Comment by:</strong> {{ comment.commentBy }}</p>
          <p><strong>Time:</strong> {{ comment.insertTime }}</p>
          <p><strong>Rating:</strong> {{ comment.rating }}</p>
          <p><strong>Text:</strong> {{ comment.text }}</p>
        </div>
      </div>
      <div [innerHTML]="iframeHtml"></div>
    </div>
    <app-reservation></app-reservation>
    <br />
    <br />
    <br />
    <br />
    <div class="footer">
      <p>&copy; 2024 pravi majstor. All rights reserved.</p>
    </div>
  </body>
</html>
