import { Router } from '@angular/router';
import { BackendService } from '../services/backend.service';
import { User } from '../models/User';
import { Restaurant } from '../models/Restaurant';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css'],
})
export class CustomerComponent {
  constructor(
    private http: HttpClient,
    private router: Router,
    public services: BackendService
  ) {}

  ngOnInit() {
    if (this.services.activeUser.accountTypeId === '') {
      this.router.navigate(['']);
    }
  }
  goToDeliveryView() {
    this.router.navigate(['delivery-view']);
  }
  goToReservarionView() {
    this.router.navigate(['reservation-view']);
  }
  goToChangePassword() {
    this.router.navigate(['change-password']);
  }

  goToProfile() {
    this.router.navigate(['profile']);
  }

  goToRestaurants() {
    this.router.navigate(['restaurant-view']);
  }
  logout() {
    this.services.activeUser = new User(
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      ''
    );
    this.router.navigate(['']);
  }
  toggleBasket() {
    this.router.navigate(['basket']);
  }
  searchName: string = '';
  searchAddress: string = '';
  searchType: string = '';
  filteredRestaurants() {
    return this.services.homeData?.restaurants.filter((restaurant) => {
      return (
        restaurant.name.toLowerCase().includes(this.searchName.toLowerCase()) &&
        restaurant.address
          .toLowerCase()
          .includes(this.searchAddress.toLowerCase()) &&
        restaurant.type.toLowerCase().includes(this.searchType.toLowerCase())
      );
    });
  }

  sortDirection: 'asc' | 'desc' = 'asc'; // Initialize sort direction
  sortColumn: keyof Restaurant | null = null; // Initialize sortColumn with null
  toggleSort(column: keyof Restaurant) {
    if (this.sortColumn === column) {
      // Reverse the sort direction if same column is clicked again
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      // Set the new column to sort
      this.sortColumn = column;
      this.sortDirection = 'asc'; // Reset sort direction to ascending
    }

    // Perform sorting based on this.sortColumn
    this.sortData();
  }

  sortData() {
    if (this.sortColumn && this.services.homeData?.restaurants) {
      this.services.homeData.restaurants.sort((a, b) => {
        const isAsc = this.sortDirection === 'asc' ? 1 : -1;
        return (a[this.sortColumn!] > b[this.sortColumn!] ? 1 : -1) * isAsc;
      });
    }
  }

  getStarsArray(rating: string): number[] {
    if (!rating || rating.trim() === '') {
      return []; // Return empty array if no rating or rating is empty string
    }

    const ratingNumber = parseFloat(rating); // Convert string to number
    if (isNaN(ratingNumber)) {
      return []; // Return empty array if rating is not a valid number
    }

    const fullStars = Math.floor(ratingNumber); // Get the integer part (number of full stars)
    const halfStars = ratingNumber % 1 >= 0.5 ? 1 : 0; // Check if there should be a half star
    const totalStars = fullStars + halfStars;

    return Array(totalStars)
      .fill(0)
      .map((x, i) => i + 1);
  }

  goToRestaurantDetails(id: string) {
    this.services.currentRestaurantBeingViewedId = id;
    this.router.navigate(['restaurant-view']);
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Simple Page with Header</title>
    <style></style>
  </head>
  <body>
    <header>
      <div class="logo">Restaurant of cringe</div>
      <div class="buttons">
        <button (click)="goToDeliveryView()">Deliveries</button>
      </div>
      <div class="buttons">
        <button (click)="goToReservarionView()">Reservations</button>
      </div>
      <div class="buttons">
        <button (click)="goToChangePassword()">Change password</button>
      </div>
      <div class="buttons">
        <button (click)="goToProfile()">Profile</button>
      </div>
      <!-- <div class="buttons">
        <button (click)="goToRestaurants()">Restaurants</button>
      </div> -->
      <div class="buttons">
        <button (click)="logout()">Logout</button>
      </div>
      <div class="buttons">
        <button (click)="toggleBasket()">Basket</button>
      </div>
    </header>
    <div *ngIf="services.homeData">
      <div class="summary">
        <p>
          Number of Restaurants: {{ services.homeData.numberOfRestaurants }}
        </p>
        <p>
          Number of Registered Users:
          {{ services.homeData.numberOfRegisteredUsers }}
        </p>
        <p>
          Number of Waiting Users: {{ services.homeData.numberOfWaitingUsers }}
        </p>
        <p>
          Number of Reservations (last 24h):
          {{ services.homeData.numberOfReservations24h }}
        </p>
        <p>
          Number of Reservations (last 7 days):
          {{ services.homeData.numberOfReservations7d }}
        </p>
        <p>
          Number of Reservations (last 30 days):
          {{ services.homeData.numberOfReservations30d }}
        </p>
      </div>

      <main>
        <h2>List of Restaurants</h2>
        <div class="search-bars">
          <input
            type="text"
            placeholder="Search by Name"
            [(ngModel)]="searchName"
          />
          <input
            type="text"
            placeholder="Search by Address"
            [(ngModel)]="searchAddress"
          />
          <input
            type="text"
            placeholder="Search by Type"
            [(ngModel)]="searchType"
          />
        </div>
        <table>
          <thead>
            <tr>
              <th (click)="toggleSort('id')">ID</th>
              <th (click)="toggleSort('name')">Name</th>
              <th (click)="toggleSort('address')">Address</th>
              <th (click)="toggleSort('type')">Type</th>
              <th (click)="toggleSort('rated')">Rating</th>
              <th>Waiters</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let restaurant of filteredRestaurants()">
              <td>{{ restaurant.id }}</td>
              <td>
                <span
                  class="clickable"
                  (click)="goToRestaurantDetails(restaurant.id)"
                >
                  {{ restaurant.name }}
                </span>
              </td>
              <td>{{ restaurant.address }}</td>
              <td>{{ restaurant.type }}</td>
              <td>
                <table class="ratings-table">
                  <tr>
                    <td class="rating-cell">
                      <ng-container
                        *ngIf="getStarsArray(restaurant.rated).length === 0"
                      >
                        <span>No ratings yet</span>
                      </ng-container>
                      <ng-container
                        *ngIf="getStarsArray(restaurant.rated).length > 0"
                      >
                        <div class="rating">
                          <span
                            *ngFor="let star of getStarsArray(restaurant.rated)"
                          >
                            <span></span>
                          </span>
                        </div>
                      </ng-container>
                    </td>
                    <td class="rating-value">
                      <p>{{ restaurant.rated }}</p>
                    </td>
                  </tr>
                </table>
              </td>
              <td>
                <ul>
                  <li *ngFor="let waiter of restaurant.waiters">
                    {{ waiter.firstName }} {{ waiter.lastName }}
                  </li>
                </ul>
              </td>
            </tr>
          </tbody>
        </table>
      </main>
    </div>
    <div class="footer">
      <p>&copy; 2024 pravi majstor. All rights reserved.</p>
    </div>
  </body>
</html>
