import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../services/backend.service';
import { DomSanitizer } from '@angular/platform-browser';
import { DeliveryOrdersWaiter } from '../models/DeliveryOrdersWaiter';
import { User } from '../models/User';

@Component({
  selector: 'app-waiter-deliveries-accept',
  templateUrl: './waiter-deliveries-accept.component.html',
  styleUrls: ['./waiter-deliveries-accept.component.css'],
})
export class WaiterDeliveriesAcceptComponent {
  constructor(
    private http: HttpClient,
    private router: Router,
    public services: BackendService,
    private sanitizer: DomSanitizer
  ) {}

  deliveryOrders: DeliveryOrdersWaiter[] = [];
  ngOnInit() {
    this.services
      .getOrdersForRestaurantWaiter(this.services.waiterWorkAtRestaurant)
      .subscribe((response) => {
        this.deliveryOrders = response;
      });
  }
  goToDeliveryView() {
    this.router.navigate(['delivery-view']);
  }

  goToHome() {
    this.router.navigate(['waiter']);
  }

  goToReservarionView() {
    this.router.navigate(['reservation-view']);
  }

  goToChangePassword() {
    this.router.navigate(['change-password']);
  }

  goToProfile() {
    this.router.navigate(['profile']);
  }

  toggleBasket() {
    this.router.navigate(['basket']);
  }

  goToRestaurants() {
    this.router.navigate(['restaurant-view']);
  }

  logout() {
    this.services.activeUser = new User(
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      ''
    );
    this.router.navigate(['']);
  }
  goToDeliveryOrdersWaiter() {
    this.router.navigate(['waiter-orders-accept']);
  }
  goToDiagrams() {
    this.router.navigate(['diagrams']);
  }

  acceptingOrderId: string | null = null;
  deliveryTime: string = '';

  acceptOrder(order: DeliveryOrdersWaiter): void {
    this.acceptingOrderId = order.orderId;
    this.deliveryTime = ''; // Clear previous delivery time selection
  }

  denyOrder(order: DeliveryOrdersWaiter): void {
    console.log(`Order ${order.orderId} denied.`);
    this.services
      .updateOrderStatusWaiter('130', '', order.orderId)
      .subscribe((response) => {
        this.services
          .getOrdersForRestaurantWaiter(this.services.waiterWorkAtRestaurant)
          .subscribe((response) => {
            this.deliveryOrders = response;
          });
      });
  }

  confirmAccept(order: DeliveryOrdersWaiter): void {
    if (this.deliveryTime) {
      console.log(
        `Order ${order.orderId} accepted with delivery time: ${this.deliveryTime} minutes.`
      );
      this.services
        .updateOrderStatusWaiter('110', this.deliveryTime, order.orderId)
        .subscribe((response) => {
          this.services
            .getOrdersForRestaurantWaiter(this.services.waiterWorkAtRestaurant)
            .subscribe((response) => {
              this.deliveryOrders = response;
            });
        });
      this.acceptingOrderId = null; // Reset accepting state
      this.deliveryTime = ''; // Clear delivery time selection
    } else {
      alert('Please select a delivery time frame.');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Waiter Dashboard</title>
  </head>
  <body>
    <header>
      <div class="logo" (click)="goToHome()">Konobar</div>
      <!-- <div class="buttons">
        <button (click)="goToDeliveryView()">Deliveries</button>
      </div> -->
      <!-- <div class="buttons">
        <button (click)="goToReservarionView()">Reservations</button>
      </div> -->
      <!-- <div class="buttons">
        <button (click)="goToChangePassword()">Change password</button>
      </div> -->
      <div class="buttons">
        <button (click)="goToProfile()">Profile</button>
      </div>
      <div class="buttons">
        <button (click)="goToDeliveryOrdersWaiter()">Delivery</button>
      </div>
      <div class="buttons">
        <button (click)="goToDiagrams()">Diagrams</button>
      </div>
      <div class="buttons">
        <button (click)="logout()">Logout</button>
      </div>
      <!-- <div class="buttons">
        <button (click)="toggleBasket()">Basket</button>
      </div> -->
    </header>
    <br />

    <div class="order-list">
      <div *ngFor="let order of deliveryOrders" class="order-item">
        <h3>Order ID: {{ order.orderId }}</h3>
        <p><strong>Ordered When:</strong> {{ order.orderedWhen }}</p>
        <p><strong>Ordered By:</strong> {{ order.orderedBy }}</p>
        <p><strong>Contact Phone:</strong> {{ order.contactPhone }}</p>

        <div *ngIf="order.orderDishList.length > 0">
          <p><strong>Ordered Dishes:</strong></p>
          <ul>
            <li *ngFor="let dish of order.orderDishList">
              {{ dish.amount }} x {{ dish.name }}
            </li>
          </ul>
        </div>
        <div *ngIf="order.orderDishList.length === 0">
          <p><strong>No dishes ordered.</strong></p>
        </div>

        <div class="action-buttons">
          <button (click)="acceptOrder(order)">Accept</button>
          <button (click)="denyOrder(order)">Deny</button>
          <div
            *ngIf="acceptingOrderId === order.orderId"
            class="accept-options"
          >
            <select [(ngModel)]="deliveryTime">
              <option value="">Select delivery time frame</option>
              <option value="20-30">20-30 minutes</option>
              <option value="30-40">30-40 minutes</option>
              <option value="50-60">50-60 minutes</option>
            </select>
            <button (click)="confirmAccept(order)">Confirm</button>
          </div>
        </div>
      </div>
    </div>
    <br />
    <br />
    <br />
    <br />
    <div class="footer">
      <p>&copy; 2024 pravi majstor. All rights reserved.</p>
    </div>
  </body>
</html>
