import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../services/backend.service';
import { User } from '../models/User';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {
  constructor(
    private http: HttpClient,
    private router: Router,
    private services: BackendService
  ) {}

  message: string = '';
  statusId: string = '';
  creditCardNo: string = '';
  profilePicture: string =
    'data:image/jpeg;base64,';

  email: string = '';
  contactPhone: string = '';
  sex: string = '';
  lastName: string = '';
  firstName: string = '';
  securityA: string = '';
  securityQ: string = '';
  accountTypeId: string = '';
  password: string = '';
  passwordClear: string = '';
  username: string = '';
  address: string = '';
  profilePictureWidth: number = 300;
  profilePictureHeight: number = 300;

  userExists: boolean = true;

  existingUser: User | undefined;

  emailPattern: RegExp = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  isEmailValid(): boolean {
    return this.emailPattern.test(this.email);
  }
  onFileSelected(event: any) {
    this.message = '';
    const file: File = event.target.files[0];

    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        const image = new Image();
        image.src = reader.result as string;
        image.onload = () => {
          const width = image.width;
          const height = image.height;
          // console.log(reader.result as string);
          console.log(width + '    ' + height);
          if (width >= 100 && height >= 100 && width <= 300 && height <= 300) {
            this.profilePicture = reader.result as string;
            this.profilePictureWidth = width;
            this.profilePictureHeight = height;
            console.log(this.profilePicture);
          } else {
            this.profilePictureWidth = width;
            this.profilePictureHeight = height;
            this.message = 'slika je ili premala ili prevelika';
          }
        };
      };
    }
  }

  register() {
    this.message = '';
    // Perform registration logic here

    if (
      this.profilePictureWidth >= 100 &&
      this.profilePictureHeight >= 100 &&
      this.profilePictureWidth <= 300 &&
      this.profilePictureHeight <= 300
    ) {
      console.log('Registration data:', {
        statusId: this.statusId,
        creditCardNo: this.creditCardNo,
        profilePicture: this.profilePicture,
        email: this.email,
        contactPhone: this.contactPhone,
        sex: this.sex,
        lastName: this.lastName,
        firstName: this.firstName,
        securityA: this.securityA,
        securityQ: this.securityQ,
        accountTypeId: this.accountTypeId,
        password: this.password,
        passwordClear: this.passwordClear,
        username: this.username,
        address: this.address,
      });

      this.services
        .getUserByEmailOrUsername(this.email, this.username)
        .subscribe((data: User) => {
          console.log(data);
          if (data == null) {
            this.message = '';
            alert('cestitam user ne postoji');

            this.services
              .createUser(
                new User(
                  '0',
                  this.creditCardNo,
                  this.profilePicture,
                  this.email,
                  this.contactPhone,
                  this.sex,
                  this.lastName,
                  this.firstName,
                  this.securityA,
                  this.securityQ,
                  '1',
                  this.password,
                  this.password,
                  this.username,
                  this.address
                )
              )
              .subscribe((response) => {
                alert('bravo.');
              });
            this.router.navigate(['']);
          } else {
            this.message = 'User already exists with username or email entered';
          }
        });
    } else {
      this.message = 'slika je i dalje losa';
    }

    // console.log(this.userExists);
    // console.log(this.existingUser);
  }
}
